mapper.select:
SELECT e.emp_no AS empNo, e.emp_id AS empId, e.emp_name AS empName, d.dept_name AS deptName, p.dept_position AS deptPosition, e.emp_start_date AS empStartDate FROM employees e JOIN departments d ON e.dept_no = d.dept_no JOIN positions p ON e.position_no = p.position_no
AND d.dept_no = #{dept}
AND e.emp_id LIKE '%' || #{empId} || '%'
AND e.emp_name LIKE '%' || #{empName} || '%'
AND e.emp_start_date BETWEEN #{startDate} AND #{endDate}
