mapper.select:
SELECT E.EMP_NO AS empNo, E.EMP_ID AS empId, E.EMP_NAME AS empName, D.CMN_DETAIL_NAME AS deptName, P.CMN_DETAIL_NAME AS deptPosition, E.EMP_START_DATE AS empStartDate FROM EMPLOYEES E JOIN COMMONCODE_DETAIL D ON TO_CHAR(E.DEPT_NO) = D.CMN_DETAIL_CODE JOIN COMMONCODE_DETAIL P ON TO_CHAR(E.POSITION_NO) = P.CMN_DETAIL_CODE
AND D.CMN_DETAIL_CODE = #{dept}
AND E.EMP_ID LIKE '%' || #{empId} || '%'
AND E.EMP_NAME LIKE '%' || #{empName} || '%'
AND E.EMP_START_DATE BETWEEN #{startDate} AND #{endDate}
