mapper.update:
UPDATE SALES_ORDER 


            SO_DATE = TO_DATE(#{value}, 'YYYY-MM-DD')


        SHIP_DATE = TO_DATE(#{value}, 'YYYY-MM-DD')


        WHS_CODE = #{value}


        PDT_CODE = #{value}


        CLT_CODE = #{value}


        SO_QUANTITY = CAST(#{value} AS NUMBER)


        PDT_SHIPPING_PRICE = CAST(#{value} AS NUMBER)


        SO_STATUS = #{value}


WHERE so_no = #{soNo}

mapper.select:
SELECT SO.SO_NO, SO.SO_CODE, SO.SO_DATE, SO.SHIP_DATE, SO.SO_QUANTITY, SO.PDT_SHIPPING_PRICE, SO.SO_VALUE, SO.SO_STATUS, SO.SO_COMMENTS, C.CLT_NO, C.CLT_CODE, C.CLT_NAME, P.PDT_NO, P.PDT_CODE, P.PDT_NAME, W.WHS_NO, W.WHS_CODE, W.WHS_NAME, E.EMP_NO, E.EMP_ID, E.EMP_NAME, S.STATUS_CODE, S.STATUS_NAME FROM SALES_ORDER SO LEFT JOIN CLIENTS C ON SO.CLT_CODE = C.CLT_CODE LEFT JOIN EMPLOYEES E ON SO.EMP_NO = E.EMP_NO LEFT JOIN PRODUCTS P ON SO.PDT_CODE = P.PDT_CODE LEFT JOIN WAREHOUSES W ON SO.WHS_CODE = W.WHS_CODE LEFT JOIN SALES_ORDER_STATUS S ON SO.SO_STATUS = S.STATUS_CODE
C.CLT_TYPE = 'ORDER'
AND SO.SO_CODE = #{soCode}
AND SO.SO_NO = #{soNo}
AND SO.PDT_CODE = #{pdtCode}
AND C.CLT_CODE LIKE '%' || #{cltCode} || '%'
AND SO.SO_STATUS = #{soStatus}
AND SO.SO_DATE BETWEEN TO_DATE(#{startDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{endDate, jdbcType=VARCHAR}, 'YYYY-MM-DD')
AND SO.SHIP_DATE BETWEEN TO_DATE(#{startDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{endDate, jdbcType=VARCHAR}, 'YYYY-MM-DD')
ORDER BY SO.SO_NO DESC
